mapper.select:
/* MovieMapper.getSimpleMovieInfo */
SELECT X.movieCd
     , X.movieNm
     , X.showTm
FROM tb_movie X
WHERE X.movieCd = #{movieCd}

/* MovieMapper.checkMovieGrade */
SELECT REPLACE(CONCAT('grade', grade), '.', '_') AS 'grade',count(grade) AS 'gradeCount'
FROM tb_movie_grade
WHERE account = #{account}
AND movieCd = #{movieCd}
UNION
SELECT #{grade} AS 'grade', ${grade} AS 'gradeCount'
FROM tb_grade_report
WHERE movieCd = #{movieCd}

mapper.insert:
/* MovieMapper.insertMovieGrade */
INSERT INTO tb_grade_report ( movieCd, ${grade})
VALUES ( #{movieCd}, 1 )

mapper.update:
/* MovieMapper.updateMovieGrade */
UPDATE tb_grade_report
SET ${oldGrade} = ${oldGradecCount}-1
    , ${newGrade} = ${newGradeCount}+1
WHERE movieCd = #{movieCd}
