mapper.insert:
/* UserMapper.signup */
SELECT ( CASE WHEN LASTVAL(tb_user_seq) = 999999 THEN 'can_not_insert' WHEN LENGTH(NEXTVAL(tb_user_seq)) = 1 THEN CONCAT('cineuser-00000', NEXTVAL(tb_user_seq)) WHEN LENGTH(NEXTVAL(tb_user_seq)) = 2 THEN CONCAT('cineuser-0000', NEXTVAL(tb_user_seq)) WHEN LENGTH(NEXTVAL(tb_user_seq)) = 3 THEN CONCAT('cineuser-000', NEXTVAL(tb_user_seq)) WHEN LENGTH(NEXTVAL(tb_user_seq)) = 4 THEN CONCAT('cineuser-00', NEXTVAL(tb_user_seq)) WHEN LENGTH(NEXTVAL(tb_user_seq)) = 5 THEN CONCAT('cineuser-0', NEXTVAL(tb_user_seq)) ELSE CONCAT('cineuser-', NEXTVAL(tb_user_seq)) END ) AS cineToken
INSERT INTO tb_user VALUES (#{cineToken}, #{apiToken}, #{account}, #{password}, #{userName}, 0)

mapper.select:
/* UserMapper.validSignup */
SELECT COUNT(cineToken)
FROM tb_user
WHERE account = #{param}

mapper.update:
/* UserMapper.updateUserInfo */
UPDATE tb_user
SET userName = #{userName}
WHERE (account = #{account} AND password = #{password})
   OR cineToken = #{cineToken}

mapper.select:
/* UserMapper.checkMovieGrade */
SELECT grade
FROM tb_movie_grade X
JOIN tb_user Y
ON X.account = Y.account
WHERE X.movieCd = #{movieCd}


        AND Y.cineToken = #{cineToken}


        AND Y.account = #{account} AND Y.password = #{password}

/* UserMapper.checkTotalWatchTime */
SELECT totalWatchTime
FROM tb_user
WHERE 1=1


        AND cineToken = #{cineToken}


        AND account = #{account} AND password = #{password}

mapper.update:
/* UserMapper.updateUserMovieGrade */
UPDATE tb_movie_grade
SET grade = #{grade}
WHERE account = #{account}
AND movieCd = #{movieCd}

mapper.delete:
/* UserMapper.deleteUserMovieGrade */
DELETE FROM tb_movie_grade
WHERE account = #{account}
AND movieCd = #{movieCd}

mapper.update:
/* UserMapper.countUpWatchTime */

    SELECT showTm
    FROM tb_movie
    WHERE movieCd = #{movieCd}

UPDATE tb_user
SET totalWatchTime = #{totalWatchTime} + #{showTm}
WHERE 1=1


        AND cineToken = #{cineToken}


        AND account = #{account} AND password = #{password}

/* UserMapper.countDownWatchTime */

    SELECT showTm
    FROM tb_movie
    WHERE movieCd = #{movieCd}

UPDATE tb_user
SET totalWatchTime = #{totalWatchTime} - #{showTm}
WHERE 1=1


        AND cineToken = #{cineToken}


        AND account = #{account} AND password = #{password}

mapper.select:
/* UserMapper.listGradeList */
SELECT grade, count(grade) AS count
FROM tb_movie_grade
WHERE account = #{account}
GROUP BY grade

/* UserMapper.getGradeAndReview */
SELECT X.account, X.movieCd, X.grade, Y.review
FROM tb_movie_grade X
JOIN tb_movie_review Y
ON ( X.account = Y.account
    AND X.movieCd = Y.movieCd)
WHERE X.account = #{account}
    AND X.movieCd = #{movieCd}

/* UserMapper.listUserMovieGrade */
SELECT X.grade, Y.movieCd, Y.movieNm
FROM tb_movie_grade X
JOIN tb_movie Y
ON X.movieCd = Y.movieCd
WHERE X.account = #{account}

/* UserMapper.listUserMovieReview */
SELECT X.review, Y.movieCd, Y.movieNm
FROM tb_movie_review X
JOIN tb_movie Y
ON X.movieCd = Y.movieCd
WHERE X.account = #{account}
